import React,{useState} from 'react'
import {useDispatch } from 'react-redux'
function Blog(prop) {
 const item=prop.item
  const dispatch=useDispatch()
  const [showmore,setShowmore]=useState(false)
  const toggleMore=()=>{
    setShowmore((prev)=>!prev)
  }
  console.warn(item)
  return (
    <div key={item.id} className='grid gri'>
        <div>
            <img src={item.image} alt='no' className='w-44 h-44'/>
        </div>
        <div >
           <div>{item.title}</div>
            <div className='text-xs font-light cursor-pointer'  onClick={toggleMore}>
            {showmore ? ` ${item.description}` : `${item.description.substring(0, 50)}...`}
             </div>
        </div>
           <div>
             <div>{item.price}</div>
             <button></button>
           </div>
        
    </div>
  )
}

export default Blog
