import React from 'react'
import Nav from '../components/Nav'
import men from '../assests/men.png'
import women from '../assests/women.png'
import jewellery from '../assests/jewellery.png'
import gadget from '../assests/gadget.png'
import { NavLink } from 'react-router-dom'
import { useDispatch } from 'react-redux'
import { addcategory } from '../redux/action'
function Category() {
  const catData=[
    {
        key:1,
        category: "men's clothing",
        photo:men,
        link:"men"

    },
    
    {
        key:2,
        category: "electronics",
        photo:gadget,
        link:"electronics"
    },
    {
        key:3,
        category: "women's clothing",
        photo:women,
        link:"women"
    },
    {
        key:4,
        category: "jewelery",
        photo:jewellery,
        link:`jewelery`
    },
  ] 
  const dispatch=useDispatch()
  const help=(it)=>{
      dispatch(addcategory(it))
  }
  return (
    <div>
        <Nav/>
        <div className='grid grid-cols-2 w-10/12 mx-auto gap-x-8 gap-y-16 my-12  '>
           {
             catData.map((item)=>(
                <div className='px-8  py-3 grid grid-cols-1 place-items-center place-content-center gap-y-5  rounded-md items-center shadow-md cursor-pointer hover:scale-105 hover:shadow-xl' onClick={help(item.category)} >
                   
                     <div className='text-lg font-bold capitalize'>{item.category} </div>
                     <div> <img src={item.photo} className='w-64 h-96'/></div>
                     <button  className=' capitalize font-semibold border-2 rounded-full  text-sm p-2'><NavLink to={`${item.link}`}>Click Here</NavLink></button>
                   
                </div>
             ))
           }
        </div>
    </div>
  )
}

export default Category
